import React, { useState } from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';
import MovieCard from './MovieCard';

const recommendedMovies = [
  {
    id: 1,
    title: 'Inception',
    release_date: '2010-07-16',
    poster_path: '/9gk7adHYeDvHkCSEqAvQNLV5Uge.jpg',
  },
  {
    id: 2,
    title: 'Interstellar',
    release_date: '2014-11-07',
    poster_path: '/gEU2QniE6E77NI6lCU6MxlNBvIx.jpg',
  },
  {
    id: 3,
    title: 'The Dark Knight',
    release_date: '2008-07-18',
    poster_path: '/qJ2tW6WMUDux911r6m7haRef0WH.jpg',
  },
  {
    id: 4,
    title: 'Avengers: Endgame',
    release_date: '2019-04-24',
    poster_path: '/ulzhLuWrPK07P1YkdWQLZnQh1JL.jpg',
  },
  {
    id: 5,
    title: 'Parasite',
    release_date: '2019-05-30',
    poster_path: '/7IiTTgloJzvGI1TAYymCfbfl3vT.jpg',
  },
  {
    id: 6,
    title: 'La La Land',
    release_date: '2016-12-09',
    poster_path: '/uDO8zWDhfWwoFdKS4fzkUJt0Rf0.jpg',
  },
];

function MovieList() {
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);

  const handleSearch = async (query) => {
    try {
      setLoading(true);
      setSearched(true);
      const response = await axios.get(`http://localhost:5000/api/search?query=${query}`);
      setMovies(response.data.results);
    } catch (error) {
      console.error('Error fetching movies', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      {loading && <p>Loading...</p>}
      <div className="movie-list">
        {searched
          ? movies.map((movie) => <MovieCard key={movie.id} movie={movie} />)
          : recommendedMovies.map((movie) => <MovieCard key={movie.id} movie={movie} />)
        }
      </div>
      {!searched && (
        <p style={{ color: '#4F6272', fontWeight: 600, marginTop: '1.5rem', fontSize: '1.1rem' }}>
          Recommended for you! Try searching for your favorite movie above.
        </p>
      )}
    </div>
  );
}

export default MovieList;
